let widgetOwner = "sking.near";

const poll = props.poll ?? {};
const href = props.href;

console.log(poll);

const indexVersion = props.indexVersion ?? "3.2.0";

function sliceString(string, newStringLength) {
  if (string.length > newStringLength) {
    return string.slice(0, newStringLength) + "...";
  }
  return string;
}

const Container = styled.div`
  border-radius: 21px;
  padding: 16px 24px;
  // box-shadow: rgba(0, 0, 0, 0.1) -4px 9px 25px -6px;
  // box-shadow: rgba(0, 0, 0, 0.1) 0px 4px 12px;
  box-shadow: rgba(0, 0, 0, 0.05) 0px 6px 24px 0px, rgba(0, 0, 0, 0.08) 0px 0px 0px 1px;
`;

const Content = styled.a`
  &,
  * {
    text-decoration: none !important;
  }
  :hover h3 {
    color: #4f46e5;
  }
`;

const Heading = styled.h3`
  font-size: 18px;
  font-weight: 500;
  letter-spacing: -1px;
  transition: 200ms ease;
  color: #000;
`;

const Paragraph = styled.p`
  font-size: 14px;
  font-weight: 400;
  color: #828688;
`;

return (
  <Container className="w-100 flex-grow-1 d-flex flex-column justify-content-start">
    <div className="d-flex justify-content-between gap-3 mb-2">
      <Widget
        src={`${widgetOwner}/widget/EasyPoll.UserLabel`}
        props={{ accountId: poll.accountId, size: "sm" }}
      />
      <Widget
        src={`${widgetOwner}/widget/EasyPoll.PollMoreOptions`}
        props={{ poll: poll, href: href }}
      />
    </div>
    <Content href={href}>
      <Heading>
        {sliceString(poll.value.title, 100)}{" "}
        <OverlayTrigger
          placement={"auto"}
          overlay={
            <Tooltip id={`tooltip-iah-${poll.value.title}`}>
              Only Verified Humans
            </Tooltip>
          }
        >
          <img
            height={25}
            width={25}
            style={{
              marginBottom: 6,
              marginLeft: 6,
            }}
            src="data:image/png;base64,"
          />
        </OverlayTrigger>
      </Heading>

      <Paragraph>{sliceString(poll.value.description, 300)}</Paragraph>
      <Widget
        src={`${widgetOwner}/widget/EasyPoll.PollTags`}
        props={{ poll: poll, indexVersion, showVoteButton: true }}
      />
    </Content>
  </Container>
);
