const UnFilledButton = styled.button`
  background: #fff;
  border: 1px solid #999;
  float: left;
  font-size: 24px;
  font-weight: bold;
  line-height: 34px;
  height: 34px;
  margin-right: -1px;
  margin-top: -1px;
  padding: 0;
  text-align: center;
  width: 34px;
`;

const FilledButton = styled.button`
  background: #000;
  border: 1px solid #999;
  float: left;
  font-size: 24px;
  font-weight: bold;
  line-height: 34px;
  height: 34px;
  margin-right: -1px;
  margin-top: -1px;
  padding: 0;
  text-align: center;
  width: 34px;
`;

State.init({
  squares: Array(20).fill(Array(20).fill(false)),
});

function handleSquareClick(x, y) {
  sqrs = state.squares.filter(());
  sqrs[y][x] = true;
  State.update({
    squares: sqrs,
  });
}

function renderSquare(x, y) {
  if (state.squares[y][x] === false) {
    return (
      <UnFilledButton onClick={() => handleSquareClick(x, y)}></UnFilledButton>
    );
  } else {
    return (
      <FilledButton onClick={() => handleSquareClick(x, y)}></FilledButton>
    );
  }
}

const borderRow = {
  clear: "both",
  content: "",
  display: "table",
};

return (
  <>
    <div>Cross Stitch</div>
    <div style={{ marginTop: 10 }}>
      {state.squares.map((line, y) => (
        <div style={borderRow}>
          {line.map((item, x) => {
            if (state.squares[y][x] === false) {
              return (
                <UnFilledButton
                  onClick={() => handleSquareClick(x, y)}
                ></UnFilledButton>
              );
            } else {
              return (
                <FilledButton
                  onClick={() => handleSquareClick(x, y)}
                ></FilledButton>
              );
            }
          })}
        </div>
      ))}
    </div>
  </>
);
