const iconName = props.iconName ?? "home";
const iconSize = props.iconSize ?? "1em";
const iconColor = props.iconColor ?? "black";
const iconMargin = props.iconMargin ?? "";
const iconPadding = props.iconPadding ?? "";

const icon = `bi bi-${iconName}`;

return (
  <>
    <i
      className={icon}
      style={{
        fontSize: iconSize,
        color: iconColor,
        margin: iconMargin,
        padding: iconPadding,
      }}
    />
  </>
);
